import { Dimensions, StyleSheet } from "react-native";

export const styles = StyleSheet.create({
    cardContainer: {
        width: '100%',
        height: Dimensions.get('window').height,
      },
      titles: {
        marginTop: '30%',
        width: '100%',
        alignItems: 'center'
      },
      title: {
        fontSize: 40,
        fontWeight: 'bold'
      },
      subtitle: {
        fontSize: 16,
        color: '#5c5e62'
      },
      image: {
        width: '100%',
        height: '100%',
        resizeMode: 'cover',
        position: 'absolute',
      },
      buttonContainer: {
        position: 'absolute',
        bottom: 50,
        width: '100%'
      },
      taglineCta:{
        textDecorationLine: 'underline',
      }
})
